namespace RethinkDb.Driver.Proto {
    public enum ResponseType {
        SUCCESS_ATOM = 1,
        SUCCESS_SEQUENCE = 2,
        SUCCESS_PARTIAL = 3,
        WAIT_COMPLETE = 4,
        CLIENT_ERROR = 16,
        COMPILE_ERROR = 17,
        RUNTIME_ERROR = 18,
    }

        public static class ExtensionsForResponseType {
            public static bool IsError(this ResponseType t){
                 switch( t ){
                     case ResponseType.CLIENT_ERROR:
                        return true;
                     case ResponseType.COMPILE_ERROR:
                        return true;
                     case ResponseType.RUNTIME_ERROR:
                        return true;
                    default:
                        return false;
                }
